--
-- 表的结构 `mr_news`
--

CREATE TABLE IF NOT EXISTS `mr_news` (
  `n_id` int(36) NOT NULL AUTO_INCREMENT,
  `news_title` varchar(255) NOT NULL COMMENT '文章标题',
  `news_columnid` int(11) NOT NULL,
  `news_columnviceid` int(11) NOT NULL COMMENT '副栏目ID',
  `news_key` varchar(100) NOT NULL COMMENT '文章关键字',
  `news_tag` varchar(50) DEFAULT NULL COMMENT '文章标签',
  `news_auto` varchar(20) NOT NULL COMMENT '作者',
  `news_source` varchar(20) NOT NULL DEFAULT '未知' COMMENT '来源',
  `news_content` longtext COMMENT '新闻内容',
  `news_scontent` varchar(100) NOT NULL,
  `news_hits` int(11) NOT NULL DEFAULT '200' COMMENT '点击率',
  `news_img` varchar(100) NOT NULL COMMENT '大图地址',
  `news_time` int(11) NOT NULL,
  `news_flag` set('h','c','f','a','s','p','j','d') DEFAULT NULL COMMENT '文章属性',
  `news_zaddress` varchar(100) NOT NULL COMMENT '跳转地址',
  `news_back` int(2) NOT NULL DEFAULT '0' COMMENT '是否为回收站',
  `news_open` int(2) NOT NULL COMMENT '0代表审核不通过 1代表审核通过',
  `news_lvtype` tinyint(2) NOT NULL DEFAULT '0' COMMENT '旅游类型1=行程 2=攻略',
  PRIMARY KEY (`n_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=4310 ;

--
-- 转存表中的数据 `mr_news`
--

INSERT INTO `mr_news` (`n_id`, `news_title`, `news_columnid`, `news_columnviceid`, `news_key`, `news_tag`, `news_auto`, `news_source`, `news_content`, `news_scontent`, `news_hits`, `news_img`, `news_time`, `news_flag`, `news_zaddress`, `news_back`, `news_open`, `news_lvtype`) VALUES
(1, '永安特色小吃：何记粿条店', 5, 0, '永安特色小吃：何记粿条店,何记粿条店,火车站何记粿条,永安特', '永安特色小吃：何记粿条店,何记粿条店,火车站何记粿条,永安特', '小陌', '龙岩佳亿生活', '<p style="padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="line-height: 2em; font-size: 16px">&nbsp; &nbsp; 到过永安的朋友一定知道永安的粿条是当地小吃的一大特色，其历史悠久，色白细嫩、口感爽滑......因此，今天小编就带各位到一家具有永安特色的何记粿条店。</span></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="何记粿条店" src="/uploads/allimg/140717/6-140GG15T4605.jpg" style="width: 504px; height: 202px" /></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	老板家的粿条都是自家纯手工制作，选用早稻大米，细加工碾白纯片，磨浆蒸熟而制成的</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	&nbsp;</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14917.jpg" /><br />\r\n	&nbsp;</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<strong>粿条：<span style="color: #ff0000">5</span>元</strong></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	粿条，吃法多种多样，最普遍的吃法是把粿条用开水烫过之后放入骨头汤中做成汤粉</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	何记粿条店的骨汤汤底是选用大骨，精心熬制两个多小时，汤清而味浓。加入粿条后能品尝出浓浓的米香<br />\r\n	&nbsp;</p>\r\n<br />\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	在何记还有一种粿条的吃法也很常见，那就是拌粿条。</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14920.jpg" /></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<strong>拌粿条：<span style="color: rgb(255,0,0)">5</span>元</strong></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	老板用自己精心制作的秘制酱料调配，使得整份粿条的味道变得丰满，清凉可口。</p>\r\n<br />\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	吃粿条，一定要来点其他的配料，活肉就是其中一种，<br />\r\n	&nbsp;</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14924.jpg" style="font-family: Arial, Verdana, sans-serif; color: rgb(34,34,34); font-size: 12px" /><br />\r\n	<br />\r\n	<strong style="line-height: 2em; font-size: 16px">酱水面骨肉：<span style="color: #ff0000">15</span>元/份</strong></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	所谓&ldquo;活肉&rdquo;即面骨肉，用的是猪脸上靠近上唇的部位，因为猪成天吃，全身上下数这个部位运动量大，且瘦中带肥，不柴不腻，脆嫩爽口</p>\r\n<br />\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	如果能蘸上点黄椒，那真是爽毙了~(很辣的哦，不要说小编没有提醒！)</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	当然还有千层肠、鸭胫、鸭肠、猪心猪肺等等。</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	&nbsp;</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14922.jpg" /><br />\r\n	&nbsp;</p>\r\n<div style="text-align: center">\r\n	<strong>酱水千层肠：<span style="color: #ff0000">15</span>元/份</strong></div>\r\n<div>\r\n	&nbsp;</div>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	QQ的略带点嚼劲，没有腥味，很入味哦~~</p>\r\n<br />\r\n<br />\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14923.jpg" /><br />\r\n	<br />\r\n	<strong>酱水鸭胫：<span style="color: #ff0000">15</span>元/份</strong></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	不用多说，一个字&mdash;&mdash;脆！配粿条正好</p>\r\n<br />\r\n<br />\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14924.jpg" /><br />\r\n	<br />\r\n	<strong>酱水猪肺：<span style="color: #ff0000">15</span>元/份</strong></p>\r\n<br />\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	#p#副标题#e#<br style="max-width: 100%" />\r\n	<strong style="max-width: 100%">您有以下选择：</strong></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="background-color: rgb(255,0,0); max-width: 100%; color: rgb(255,255,255)">主食</span></p>\r\n<div>\r\n	&nbsp;</div>\r\n<div style="text-align: center">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG44208.jpg" style="width: 600px; height: 445px" /></div>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="max-width: 100%; color: rgb(255,192,0)">老板说他们家的面是长汀手工面，粉是自己制作的纯手工细粉哦~（仅此一家）</span></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	&nbsp;</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="background-color: rgb(0,176,80); max-width: 100%; color: rgb(255,255,255)">菜品</span></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG44454-50.jpg" style="width: 600px; height: 380px" /></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="background-color: rgb(227,108,9); max-width: 100%; color: rgb(255,255,255)">热菜类</span></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG44455.jpg" style="width: 600px; height: 103px" /></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="background-color: rgb(0,176,240); max-width: 100%; color: rgb(255,255,255)">汤类</span></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<span style="background-color: rgb(255,192,0); max-width: 100%; color: rgb(255,255,255)"><img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG44454.jpg" style="width: 600px; font-family: Arial, Verdana, sans-serif; height: 74px; color: rgb(34,34,34); font-size: 12px" /><br />\r\n	打包盒有加收0.5元哦~打包时要跟客服说好要不要打包盒！！</span></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	&nbsp;</p>\r\n<p style="padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<strong style="max-width: 100%">商家信息：</strong></p>\r\n<p style="padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<strong>地址：福建龙岩新罗区人民西路棕榈郡</strong></p>\r\n<p style="padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<strong>营业时间：7:00&mdash;&mdash;24:00</strong></p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	&nbsp;</p>\r\n<p style="text-align: center; padding-bottom: 0px; line-height: 2em; margin: 0px; min-height: 1.5em; padding-left: 0px; padding-right: 0px; font-family: 宋体; max-width: 100%; white-space: pre-wrap; color: rgb(62,62,62); font-size: 16px; padding-top: 0px">\r\n	<img alt="永安特色小吃：何记粿条店" src="/uploads/allimg/140717/6-140GG14913.jpg" /></p>\r\n', '到过永安的朋友一定知道永安的粿条是当地小吃的一大特色，其历史悠久，色白细嫩、口感爽滑......因此，今天小编就带各位到一家具有永安特色的何记粿条店。 老板家的粿条都是自家纯手工制作，选用早稻大米，细', 543, '/uploads/allimg/140717/6-140GG14917-lp.jpg', 1446627296, 'c,p', '', 0, 1, 0),
(2, '龙岩美食之长汀小吃——糖姜蛋', 5, 0, '长汀,糖浆蛋,米饭,红糖', '长汀,糖浆蛋,米饭,红糖', '无花果', '新浪博客', '<p>\r\n	　　长汀的女人会保养。每个月月经完了，我都要吃三天糖姜蛋。所以痛经从来都找不上我。呵呵。其实这也和长汀是四面环山，空气湿度大有关系。如果在北方这么吃糖姜蛋的话，估计得上火好几天。废话不说，我来说说做法吧。</p>\r\n<p>\r\n	　　材料：蛋，米饭(硬一点)，冰糖(红糖就更好了)，姜末(自己家做的。把姜洗干净用绳子串起来风干，然后用油炸，最后碾成粉就行了)　　</p>\r\n<center>\r\n	<img alt="" height="413" src="http://www.364000.com/uploads/allimg/140630/7-140630163232.jpg" width="550" /></center>\r\n<p>\r\n	　　1、锅里多放点油，我喜欢放猪油，比较香。呵呵。　　</p>\r\n<center>\r\n	<img alt="" height="366" src="http://www.364000.com/uploads/allimg/140630/7-140630163233.jpg" width="550" /></center>\r\n<p>\r\n	　　2、油开了后，把火调成中火，把饭倒入锅里，让它慢慢煎，自己去洗个脸。呵呵。　　</p>\r\n<center>\r\n	<img alt="" height="366" src="http://www.364000.com/uploads/allimg/140630/7-140630163233-50.jpg" width="550" /></center>\r\n<p>\r\n	　　3、米饭煎得硬硬的时候就可以了，先放到碗里。煎个荷包蛋。</p>\r\n<p>\r\n	　　4、把煎好的米饭和蛋放进锅里，加水，盖过米饭，当然多放点水也是可以的，看自己的饭量。然后加入姜末和冰糖。加多少就看自己的口味了。然后盖上盖子，大火烧开，然后换成小火，煮个十分钟就好，时间控制在米饭软了就行。　　</p>\r\n<center>\r\n	<img alt="" height="366" src="http://www.364000.com/uploads/allimg/140630/7-140630163234.jpg" width="550" /></center>\r\n<p>\r\n	　　看个成品。</p>\r\n<center>\r\n	<img alt="" height="366" src="http://www.364000.com/uploads/allimg/140630/7-140630163235.jpg" width="550" /></center>\r\n', '长汀的女人会保养。每个月月经完了，我都要吃三天糖姜蛋。所以痛经从来都找不上我。呵呵。其实这也和长汀是四面环山，空气湿度大有关系。如果在北方这么吃糖姜蛋的话，估计得上火好几天。废话不说，我来说说做法吧。', 403, '/uploads/allimg/140630/7_063016351M521.jpg', 1404117359, 'p', '', 0, 1, 0),
(3, '【莲塘牛肉火锅城】', 5, 0, '【莲塘牛肉火锅城】,龙岩有什么好吃的,龙岩哪家火锅店好吃,牛', '【莲塘牛肉火锅城】,龙岩有什么好吃的,龙岩哪家火锅店好吃,牛', '小陌', '论坛', '<p>\r\n	　　牛肉含有丰富的蛋白质，氨基酸组成比猪肉更接近人体需要，能提高机体抗病能力，对生长发育及手术后、病后调养的人在补充失血、修复组织等方面物别适宜;牛肉，&ldquo;肉中骄子&rdquo;!</p>\r\n<p>\r\n	　　So，就跟着小编来感受一下这里的牛肉美食吧!</p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095P9.png" /></p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095Q2.png" /></p>\r\n<p>\r\n	　　先来一份紫菜牛肉饭，垫垫肚子</p>\r\n<p>\r\n	　　粒粒分明的米饭略带点松软，细细品味你还能尝到牛肉的肉香及紫菜那&ldquo;大海&rdquo;的味道。即使是凉了，味道还是一样的好哦~</p>\r\n<p>\r\n	<strong>　　紫菜牛肉饭</strong></p>\r\n<p>\r\n	　　不要吃太饱哦~后面还有好多好吃的菜哦</p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095Q4.png" /></p>\r\n<p>\r\n	　　红焖牛筋，从颜色搭配上看就让人食指大动，味道就更没话说了，略带点甜味，牛筋吃起来有点软糯，嚼一会儿后又能感受到牛筋的那种韧性，微弹牙还能品到一丝青椒的鲜味，It&#39;s delicious!</p>\r\n<p>\r\n	<strong>　　红焖牛</strong></p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095Q7.png" /></p>\r\n<p>\r\n	　　五花肉杂菇，是一道小编吃后还会再点的菜，嫩滑爽口，菇吃起来除了本身的清甜，还带有猪油的香味，在味蕾中是一种很美妙的平衡</p>\r\n<p>\r\n	<strong>　　五花肉杂菇</strong></p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095Q9.png" /></p>\r\n<p>\r\n	　　当然，如果您觉得这是火锅城就应该吃火锅的话，那么以下这些牛系列不要错过哦~</p>\r\n<p style="text-align: center">\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>　　地址：龙岩国际美食城E区一层5号楼对面</strong></p>\r\n<p>\r\n	<strong>　　营业时间：11:00&mdash;&mdash;21:00</strong></p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095R4.png" /></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center">\r\n	<img alt="" src="/uploads/allimg/140715/6-140G5095R6.png" /></p>\r\n', '牛肉含有丰富的蛋白质，氨基酸组成比猪肉更接近人体需要，能提高机体抗病能力，对生长发育及手术后、病后调养的人在补充失血、修复组织等方面物别适宜;牛肉，肉中骄子! So，就跟着小编来感受一下这里的牛肉美食', 317, '/uploads/allimg/140715/6-140G5095P9-lp.png', 1446706997, 'p', '', 0, 1, 0),
(4, '六一广场黄鹤楼：美味泡椒田鸡等你来', 5, 0, '六一广场黄鹤楼：美味泡椒田鸡等你来,黄鹤楼,泡椒田鸡,黄鹤楼', '六一广场黄鹤楼：美味泡椒田鸡等你来,黄鹤楼,泡椒田鸡,黄鹤楼', '小陌', '龙岩网', '<p>\r\n	　　黄鹤楼的泡椒田鸡是我非常喜爱的一道菜，田鸡肉质细嫩、味道鲜美，和泡椒一起烹制，鲜香开胃，让人回味无穷。而且田鸡含有丰富的蛋白质，对于身体增加能量的吸收和提高免疫力都有很好的功效。</p>\r\n<center>\r\n	<img alt="按此在新窗口浏览图片" height="400" src="http://www.364000.com/GImages/2012115/201211581995.jpg" width="600" /><br />\r\n	&nbsp;</center>\r\n<center>\r\n	<img alt="按此在新窗口浏览图片" height="400" src="http://www.364000.com/GImages/2012115/201211581996.jpg" width="600" /><br />\r\n	<br />\r\n	<img alt="" src="/uploads/allimg/140725/6-140H5113138.jpg" style="height: 356px; width: 600px;" /></center>\r\n<p>\r\n	<strong>　　小编推荐：</strong>精美打包盒/5元一个，用这种打包盒打包田鸡不但可以保温也不会破坏泡椒田鸡的美观，在家里也能享受到店里的色香味俱全，还可以放入微波炉，洗干净还能循环使用呢，是个很不错的选择哦。</p>\r\n<p>\r\n	<strong>　　您有以下选择哦：</strong></p>\r\n<p style="text-align: center;">\r\n	<span style="font-size:16px;"><span style="color:#000000;"><strong><span style="background-color:#ffff00;">　　特色菜：</span></strong></span></span></p>\r\n<p style="text-align: center;">\r\n	　　泡椒田鸡 -------- <strong><span style="color:#ff0000;">30/斤</span></strong>(2斤起)<br />\r\n	<br />\r\n	<span style="font-size: 12px;">　　痴狂红烧小龙虾----------------- </span><strong style="font-size: 12px;"><span style="color:#ff0000;">90/2斤</span></strong><br />\r\n	<br />\r\n	<span style="font-size: 12px;">　　水煮活鱼 --------------------- </span><strong style="font-size: 12px;"><span style="color:#ff0000;">20元/斤</span></strong><br />\r\n	<br />\r\n	<span style="font-size: 12px;">　泡椒海鲜 --------------------- </span><span style="font-size: 12px; color: rgb(255, 0, 0);"><strong>90元</strong></span><br />\r\n	<br />\r\n	<span style="font-size: 12px;">　　酱油水黄瓜鱼 ------------------ </span><strong style="font-size: 12px;"><span style="color:#ff0000;">70元</span></strong><br />\r\n	<br />\r\n	<span style="font-size: 12px;">　　手撕高丽菜 ------------------- </span><strong style="font-size: 12px;"><span style="color:#ff0000;">25元</span></strong><br />\r\n	<br />\r\n	<span style="font-size: 12px;">　　龙须菜 --------------------- </span><strong style="font-size: 12px;"><span style="color:#ff0000;">25元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　龙蛇豆 --------------------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　目鱼花(爆炒、铁板)-------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　稻香肉 --------------------- <strong><span style="color:#ff0000;">58元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　松户牛腩 --------------------- <strong><span style="color:#ff0000;">45元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　干蒸鸡 --------------------- 时价</p>\r\n<p style="text-align: center;">\r\n	　　新泉八猪品 -------------------- <strong><span style="color:#ff0000;">48元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　香辣蟹脚 --------------------- <strong><span style="color:#ff0000;">48元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　酸辣牛蹄筋 --------------------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　红烧牛筋 --------------------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　椒盐排骨 --------------------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　金牌蒜香骨 --------------------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　香炸松阪肉 --------------------- <strong><span style="color:#ff0000;">38元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　脆椒松阪肉 --------------------- <strong><span style="color:#ff0000;">45元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　砂锅鱼杂 --------------------- <strong><span style="color:#ff0000;">48元</span></strong></p>\r\n<p style="text-align: center;">\r\n	　　脆椒鸡软骨 --------------------- <strong><span style="color:#ff0000;">45元</span></strong></p>\r\