INSERT INTO `mr_news` (`n_id`, `news_title`, `news_columnid`, `news_columnviceid`, `news_key`, `news_tag`, `news_auto`, `news_source`, `news_content`, `news_scontent`, `news_hits`, `news_img`, `news_time`, `news_flag`, `news_zaddress`, `news_back`, `news_open`, `news_lvtype`) VALUES
(24, '长汀特色美食', 5, 0, '长汀,美食,长汀特色美食', '长汀,美食,长汀特色美食', 'xiaoxian', '龙岩网', '<div style="text-align: center; ">\r\n	<img alt="" src="/uploads/allimg/140814/107-140Q4163224.jpg" /></div>\r\n<p style="text-align: center; ">\r\n	　　白斩河田鸡</p>\r\n<p>\r\n	　　长汀河田鸡是我国五大名鸡之一，在长汀，白斩河田鸡更是最有名气的一道菜，有&ldquo;长汀第一大菜&rdquo;之称。所以来了长汀不尝一次白斩鸡那你就是白来了!看着白斩鸡外表金黄，里面的肉质却是异常的鲜嫩，这完全得益于在整个烹饪过程中鸡肉都没有浸入水，密封干蒸的做法使得鸡肉的鲜美完全保留在鸡身中，搭配上浇淋在表面的姜葱汁一同入口，口中混杂着鸡的原味和葱姜的香辣味，真是别有一番风味!</p>\r\n<p style="text-align: center; ">\r\n	<img alt="" src="/uploads/allimg/140814/107-140Q4163225-50.jpg" /></p>\r\n<p style="text-align: center; ">\r\n	　　糯米酒</p>\r\n<p style="text-align: left; ">\r\n	　　这地道的客家米酒还未入口，那独特的浓郁的酒香就迎面而来，真是还没喝就要醉了。尝一口，满口香甜，又不至于甜得发腻;再品，在香甜之后就是极其浓郁的酒味，这酒味不像啤酒或是其他洋酒偶会呛鼻，是一种非常温和纯净的酒香，在入喉后升腾起来，光是一口就让人回味非常!</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p style="text-align: center; ">\r\n	<img alt="" src="/uploads/allimg/140814/107-140Q4163231.jpg" />　　</p>\r\n<p style="text-align: center; ">\r\n	南瓜饼</p>\r\n<p style="text-align: left; ">\r\n	　　乍看这南瓜饼，色泽金黄，一口下去先是咬中了被表层包裹着的南瓜泥混合着糯米粉的部分，软软糯糯又掺杂着酥脆的表皮，口感十分丰富;再一口，馅心的豆沙流入口中，香甜软糯，让人忍不住一口接一口一个接一个地吃下去。</p>\r\n<p style="text-align: center; ">\r\n	<img alt="" src="/uploads/allimg/140814/107-140Q4163226.jpg" /></p>\r\n<p style="text-align: center; ">\r\n	　　腐竹豆腐汤</p>\r\n<p style="text-align: left; ">\r\n	　　长汀的豆腐有多出名我就不再多说了，就说这汤里的豆腐吧，外表白净细腻一点不输那剥了壳的鸡蛋，放入嘴里就算说是入口即化都不为过!伴着腐竹一起吃，又多了一分嚼劲。再尝这汤汁，既有豆腐和腐竹的甜味儿，又掺着葱花独特的香味儿，口感十分丰富。</p>\r\n<p style="text-align: center; ">\r\n	<img alt="" src="/uploads/allimg/140814/107-140Q4163225.jpg" /></p>\r\n<div style="text-align: center; ">\r\n	　　鲜肉焖豆腐</div>\r\n<div style="text-align: left; ">\r\n	<p>\r\n		　　用淀粉勾芡恰好将肉的鲜味儿和豆腐的甜味儿都锁在汤汁里。豆腐滑嫩，融入了肉的鲜味儿;肉煮得恰到好处，有嚼劲又不会太老，加入的青椒很好地中和了肉腥味儿;青椒清脆;洒在最上面的葱花不仅好看，更让整道菜的味道更丰富，多了层次。舀一勺的豆腐再混着汤汁和米饭，很家常的一道菜让人想起了妈妈的味道。</p>\r\n</div>\r\n<br />\r\n', '白斩河田鸡 长汀河田鸡是我国五大名鸡之一，在长汀，白斩河田鸡更是最有名气的一道菜，有长汀第一大菜之称。所以来了长汀不尝一次白斩鸡那你就是白来了!看着白斩鸡外表金黄，里面的肉质却是异常的鲜嫩，这完全得益', 234, '/uploads/allimg/140814/107-140Q4163224-lp.jpg', 1408004845, 'c,p', '', 0, 1, 0),
(25, '福建六大菜系之闽北菜系', 5, 0, '福建六大菜系之闽北菜系,福建,闽北,美食', '福建六大菜系之闽北菜系,福建,闽北,美食', '无花果', '未知', '<p style="text-align: center; ">\r\n	<strong>　　闽北菜　　</strong></p>\r\n<center>\r\n	<img alt="" height="305" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z224.jpg" width="451" /></center>\r\n<p style="text-align: center; ">\r\n	　浦城香肠　　</p>\r\n<center>\r\n	<img alt="" height="307" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z225.jpg" width="455" /></center>\r\n<p style="text-align: center; ">\r\n	　　炒米果　　</p>\r\n<center>\r\n	<img alt="" height="303" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z225-50.jpg" width="435" /></center>\r\n<p style="text-align: center; ">\r\n	　　板鸭锅　　</p>\r\n<center>\r\n	<img alt="" height="309" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z225-51.jpg" width="447" /></center>\r\n<p style="text-align: center; ">\r\n	　　青椒炒泥鳅　　</p>\r\n<center>\r\n	<img alt="" height="303" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z226.jpg" width="449" /></center>\r\n<p style="text-align: center; ">\r\n	　　卤鸭头　　</p>\r\n<center>\r\n	<img alt="" height="301" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z227.jpg" width="447" /></center>\r\n<p style="text-align: center; ">\r\n	　　田螺煲　　</p>\r\n<center>\r\n	<img alt="" height="298" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z228.jpg" width="446" /></center>\r\n<p style="text-align: center; ">\r\n	　　土鸭锅　　</p>\r\n<center>\r\n	<img alt="" height="299" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z228-50.jpg" width="450" /></center>\r\n<p style="text-align: center; ">\r\n	　　蛋皮燕　　</p>\r\n<center>\r\n	<img alt="" height="338" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z229.jpg" width="450" /></center>\r\n<p style="text-align: center; ">\r\n	　　苦锥果　　</p>\r\n<center>\r\n	<img alt="" height="301" src="http://www.364000.com/uploads/allimg/140816/7-140Q60Z229-50.jpg" width="449" /></center>\r\n<p style="text-align: center; ">\r\n	　葱油蛋菇</p>\r\n', '闽北菜 浦城香肠 炒米果 板鸭锅 青椒炒泥鳅 卤鸭头 田螺煲 土鸭锅 蛋皮燕 苦锥果 葱油蛋菇', 173, '/uploads/allimg/140816/7_0Q60Z551Q37.jpg', 1408150922, 'p', '', 0, 1, 0),
(26, '福建六大菜系之闽东菜', 5, 0, '福建六大菜系之闽东菜,福建,美食,闽东菜', '福建六大菜系之闽东菜,福建,美食,闽东菜', '无花果', '未知', '<p style="text-align: center; ">\r\n	<strong>　　闽东菜　　</strong></p>\r\n<center>\r\n	<img alt="" height="278" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK5.jpg" width="419" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　</p>\r\n<center>\r\n	<img alt="" height="650" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK5-50.jpg" width="156" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　</p>\r\n<center>\r\n	<img alt="" height="650" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK5-51.jpg" width="168" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　</p>\r\n<center>\r\n	<img alt="" height="650" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK6.jpg" width="165" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　</p>\r\n<center>\r\n	<img alt="" height="650" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK6-50.jpg" width="154" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　</p>\r\n<center>\r\n	<img alt="" height="650" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK6-51.jpg" width="160" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　</p>\r\n<center>\r\n	<img alt="" height="650" src="http://www.364000.com/uploads/allimg/140816/7-140Q60ZK7.jpg" width="228" /></center>\r\n', '闽东菜', 243, '/uploads/allimg/140816/7_0Q609102YI7.jpg', 1408151204, 'p', '', 0, 1, 0),
(27, '国内各地土得掉渣的美食，你吃过吗？', 5, 0, '国内各地土得掉渣的美食，你吃过吗？,国内,美食,土', '国内各地土得掉渣的美食，你吃过吗？,国内,美食,土', '无花果', '未知', '<p style="text-align: center; ">\r\n	　　<img alt="" height="372" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61331-50.jpg" width="640" /></p>\r\n<p>\r\n	　　以美食居首的中国，美食种类不胜其数，闻名于世的美食也数不胜数!但土得掉渣却美味十足的美食却很少被人们所知，小编搜集了一些土得掉渣的美食，来看看有你知道的吗?</p>\r\n<p>\r\n	　　坨坨肉　　</p>\r\n<center>\r\n	<img alt="" height="424" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61329-50.jpg" width="640" /></center>\r\n<p>\r\n	　　坨坨肉，彝语称&ldquo;乌色色脚&rdquo;， 意思是猪肉块块。坨坨肉是小凉山彝族人民吃肉食的基本制作方法。由于这种肉做法特别，又不是很肥，吃起来越嚼越香，越吃越胃口开。坨坨肉制作的诀窍是掌握适当的火候，火候不到不熟，过迟肉绵，因此，一般人不会做。彝族制作的坨坨肉，既鲜又香，别有风味，特别是选用四五斤重的仔猪肉制成，更是清脆可口，是当地人用来待客的佳品。所以，砣砣肉如今已成为小凉山的一道很有名气的风味菜。</p>\r\n<p>\r\n	　　呱呱　　</p>\r\n<center>\r\n	<img alt="" height="394" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61329-51.jpg" width="640" /></center>\r\n<p>\r\n	　　天水一带的一种特殊食品。无论是寒风骤起的严冬，还是烈日当头的盛夏，总有那紫红色的呱呱在摊点出售，食客往来不断，生意格外红火。如果说西北人喜食酸辣，那么天水呱呱可是典型的辣味小吃。若初食者，面对满碗流红的呱呱，定会咋舌、冒汗。而当地人尤喜以呱呱为早点，一些男女几乎&ldquo;不可一日无此君&rdquo;。</p>\r\n<p>\r\n	　　豌豆粉　　</p>\r\n<center>\r\n	<img alt="" height="402" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61330.jpg" width="600" /></center>\r\n<p>\r\n	　　上次去昆明找站长的时候，他带我去吃过地地道道的昆明特色小吃!叫做&ldquo;老两口豌豆粉&rdquo;(是老爷爷和老奶奶开的)。是用黄豌豆制作的美食!确实是土得掉渣了，但是味道有浓郁的豌豆香味，比起吃凉粉要美味好多了。放上大蒜末放上一些葱花再加上酱油和白醋，还有加上一大勺辣椒还有一些炒过的花生!吃起来太爽了。</p>\r\n<p>\r\n	　　杂拌儿</p>\r\n<p style="text-align: center; ">\r\n	　　<img alt="" height="352" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61330-50.jpg" width="640" /></p>\r\n<p>\r\n	　　老北京过大年时，家家户户守岁时必吃的小食品。一提&ldquo;杂拌儿&rdquo;年轻人不明白是什么样的食品，街市上没见过有卖的呀?旧京时的&ldquo;杂拌儿&rdquo;，其实是由花生、胶枣、栗子、桃脯、蜜枣等果品掺在一起拌和而成。老北京过年时，孩童们最喜欢吃的不是那饺子和年菜儿，他们最想吃最爱吃的，是那摆在佛堂里奉神祖的&ldquo;蜜供&rdquo;和那色彩鲜艳各种味道的&ldquo;杂拌儿&rdquo;。</p>\r\n<p>\r\n	　　一套烧饵块　　</p>\r\n<center>\r\n	<img alt="" height="300" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61A9.jpg" width="400" /></center>\r\n<p>\r\n	　　同样也是去昆明的时候站长夏天烤洋芋带我去吃的!据说很多人那这个当做早点，中间夹上一根油条有的人还加一根火腿肠!抹上甜甜的甜酱或者是抹上辣辣的特质辣酱，还可以放上昆明地道的酸腌菜，还有豆芽菜，韭菜地道还有昆明地道腐乳!把饵块铺开烧一会儿就抹上这些然后卷上油条，咬一口油条的香脆，饵块的糯糯的口感黏牙齿，甜酱里面还有芝麻炒香的味道隐隐约约在你嘴里回荡。</p>\r\n<p>\r\n	　　夏天烤洋芋说他以前上学的时候早点经常吃这个呢。算是昆明标准的一些早点种类了。三四块钱一份还是挺划算的美味。#p#副标题#e#</p>\r\n<p>\r\n	　　搅团　　</p>\r\n<center>\r\n	<img alt="" height="372" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61331-50.jpg" width="640" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="376" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61331-51.jpg" width="640" /></center>\r\n<p>\r\n	　　西北地区一特色吃食，根据主要用料不同，分为荞面搅团、玉米搅团和洋芋搅团，陕北用荞面做搅团，更筋道味美。在西北，有一种说法：谁家娶的媳妇儿贤不贤惠，是要看看她打的搅团光不光或筯道不筯道。一碗酸油菜汤，一小油碟，一大碗玉米搅团，边吃搅团边喝菜汤，香喷喷，辣乎乎，油渍渍，酸溜溜，咸丝丝的，五味杂陈，更具民族特色。</p>\r\n<p>\r\n	　　胡辣汤　　</p>\r\n<center>\r\n	<img alt="" height="488" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61332.jpg" width="640" /></center>\r\n<p>\r\n	　　胡辣汤，又名糊辣汤，河南传统早餐。是中国北方早餐中常见的汉族传统汤类名吃。由多种天然中草药按比例配制的汤料在加入胡椒和辣椒又用骨头汤做底料，其特点是微辣，营养丰富，味道上口，汤味浓郁、汤色靓丽、汤汁粘稠，香辣可口，十分适合配合其它早点进餐。目前，已经发展成为河南人和陕西人都喜爱和知晓的小吃之一。</p>\r\n<p>\r\n	　　洋芋擦擦　　</p>\r\n<center>\r\n	<img alt="" height="396" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61332-50.jpg" width="640" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="365" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61333.jpg" width="640" /></center>\r\n<p>\r\n	　　洋芋擦擦是陕北、山西晋西，甘肃陇东等地的传统饭食之一，洋芋其实就是土豆，擦擦是一种工具，用来擦土豆丝的。以土豆为主料，加少许白面加工制成，深得陕北人喜爱。色泽金黄的土豆泛着淡淡的油光，土豆的清香洋溢开来，吃上一口，既有薯条的口感和嚼头，又有肉末红椒的鲜香。洋芋擦擦属天然绿色食品，一年四季均可食用。</p>\r\n<p>\r\n	　　印子粑　　</p>\r\n<center>\r\n	<img alt="" height="571" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61333-50.jpg" width="640" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="192" src="http://p2.pstatp.com/large/1157/7421386770" width="640" /></center>\r\n<p>\r\n	　　印子粑是湖北大治的汉族民俗食品，每年制作两次，一次在春节前,一次在二月花朝后。粑分两种,一种是高梁粑,红色,代表日子越过越红火;一种是白粑,蒸熟后在粑中点红,代表丰收、喜庆和圣洁。印子粑也有绿色的,表示期望来年风调雨顺,禾苗葱壮。粑印用木制，形状一般只有小茶杯口大。粑印里刻有反向的如马、牛、羊、鸡、龙、犬以及鸟雀、花、草等各种动植物图案，形象生动，栩栩如生。堆成的粑山，晶莹透亮，造型美观，堪称优秀的民间艺术品。#p#副标题#e#</p>\r\n<p>\r\n	　　钱钱饭　　</p>\r\n<center>\r\n	<img alt="" height="389" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61334-50.jpg" width="640" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="216" src="http://p0.pstatp.com/large/1152/7710383334" width="640" /></center>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	　　提起陕北的钱钱饭，那在陕北可是家喻户晓，人人皆知。家庭主妇人人会做，在陕北生活过的人也都吃过。钱钱饭是一种稀饭，黑豆经浸泡，膨圆胀大，放在碾上压成片，形如铜钱，所以百姓称&rdquo;钱钱饭&rdquo;。钱钱饭营养丰富，易于消化，夏有止渴消暑作用，冬有保温驱寒功效。</p>\r\n<p>\r\n	　　烟台焖子　　</p>\r\n<center>\r\n	<img alt="" height="480" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61335-50.jpg" width="640" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="433" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61336.jpg" width="640" /></center>\r\n<p>\r\n	　　烟台焖子是山东烟台著名的汉族小吃，用粗制地瓜淀粉做原料，将淀粉加水加热打冻，将冻切成小块，用锅煎到外边成焦状颜色由白变透明，并佐以虾油、芝麻酱、蒜汁等调料上桌即可。曾经，焖子非常受港城市民的青睐，凡是有小吃的地方总少不了焖子的身影;如今，小吃街上遍寻不见烟台焖子，但却出现在酒店中。烟台商校烹饪专业的曹老师认为，小吃有小吃的经营特点，焖子还是应该走大众化路线。望焖子能回到小吃界!</p>\r\n<p>\r\n	　　修水哨子　　</p>\r\n<center>\r\n	<img alt="" height="409" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61336-50.jpg" width="640" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="373" src="http://www.364000.com/uploads/allimg/140818/7-140QQ61337.jpg" width="640" /></center>\r\n<p>\r\n	　　修水哨子是江西省九江市修水县一种历史悠久而富有特色的汉族传统名点。&ldquo;哨&rdquo;字应该是&ldquo;食&rdquo;字偏旁，电脑里没有这个字，只有用&ldquo;哨子&rdquo;了。那个&ldquo;哨子&rdquo;的&ldquo;哨&rdquo;(sh&agrave;o)字是修水人根据方言生造的，字典中是没有这个字的。到修水的人无不想美餐一顿哨子，而好客的修水人常以哨子佳肴来礼待客人。只要是亲口尝过哨子的人，无不为其皮嫩、馅香、爽口而称赞不已。</p>\r\n', '以美食居首的中国，美食种类不胜其数，闻名于世的美食也数不胜数!但土得掉渣却美味十足的美食却很少被人们所知，小编搜集了一些土得掉渣的美食，来看看有你知道的吗? 坨坨肉 坨坨肉，彝语称乌色色脚， 意思是猪', 391, '/uploads/allimg/140818/7_0QQ6210CM5.jpg', 1408349599, 'p', '', 0, 1, 0),
(28, '兰州只有拉面？你out啦', 5, 0, '兰州只有拉面？你out啦,兰州,美食,拉面', '兰州只有拉面？你out啦,兰州,美食,拉面', '无花果', '未知', '<p style="text-align: center; ">\r\n	　　<img alt="" height="480" src="http://www.364000.com/uploads/allimg/140818/7-140QQG448.jpg" width="480" /></p>\r\n<p>\r\n	　　先说说不加辣子和醋的甜食。</p>\r\n<p>\r\n	　　不仅南方有甜食，西北的甜食也别具一格。灰豆子是主料是豌豆，配以红枣白糖等，煮成粥状物，味道甜美，又有豆子煮绵的口感，既可冬天趁热喝，又可夏天冰镇。爱甜食的 MM 和小盆友绝对会爱不释手。</p>\r\n<p>\r\n	　　灰豆子　　</p>\r\n<center>\r\n	<img alt="" height="348" src="http://www.364000.com/uploads/allimg/140818/7-140QQG448-50.jpg" width="480" /></center>\r\n<p>\r\n	　　2.再来一发甜食。甜胚子。有点类似南方的米酒(北方的醪糟)，也是用粮食酿制的。这里用的粮食也比较有特色&mdash;&mdash;大部分用青稞。略微发酵后麦芽糖的香甜和芳醇的酒汁简直让人在夏季欲罢不能。</p>\r\n<p>\r\n	　　甜胚子　　</p>\r\n<center>\r\n	<img alt="" height="454" src="http://www.364000.com/uploads/allimg/140818/7-140QQG448-51.jpg" width="480" /></center>\r\n<p>\r\n	　　3.我继续说甜食。夏天有甜胚子和醪糟，冬天在这里有热冬果。原产黄河流域的冬果梨是热冬果的主料，是这里冬天著名的甜点。冬果梨化痰止咳辅以冰糖和胃润肺的功效，让人们在干燥寒冷的冬天得到滋养，一起熬制后汤汁甜美，果肉美味，滋补与美味兼具。流传上千年的美味有没有让你想到现在流行的饮料&mdash;&mdash;冰糖雪梨呢?</p>\r\n<p>\r\n	　　热冬果　　</p>\r\n<center>\r\n	<img alt="" height="646" src="http://www.364000.com/uploads/allimg/140818/7-140QQG449.jpg" width="480" /></center>\r\n<p>\r\n	　　4.西北甜食不够么?我们继续。百合这种东西不知道同学们是否熟悉，兰州的特产之一。即使生长在兰州，也不是所有的百合都算正宗。相传只有兰山上一个区域的百合味道甜美，才算是正宗。百合桃是将百合蒸熟，再将一片一片的百合聚拢成桃子形状，中间灌以豆沙做馅，用山楂糕点上桃尖，蒸熟后上桌前用白糖勾芡浇淋。听起来就是很甜的甜食吧...</p>\r\n<p>\r\n	　　5.南方很多甜食用糯米制，西北也有这样的美食。热晶糕就是代表之一。将糯米用水烫透，捞人软布或棕叶上，铺 3 层米、2 层枣，然后包紧用绳捆扎入锅，加水煮熟，冷却后切块撒白糖食用。记得小时候街上卖这个的小贩总会让小学门口发生严重的交通拥塞。</p>\r\n<p>\r\n	　　热晶糕　　</p>\r\n<center>\r\n	<img alt="" height="360" src="http://www.364000.com/uploads/allimg/140818/7-140QQG449-50.jpg" width="480" /><br />\r\n	#p#副标题#e#</center>\r\n<p>\r\n	　　6.如果你喝过兰州的鸡蛋牛奶醪糟，你一定不会再爱蛋花米酒了。醪糟就是南方的米酒，首先把醪糟放在牛奶中烧开，打入鸡蛋花，并撒葡萄干、枸杞、花生、核桃仁、芝麻、白糖等，其成品黄白相间，奶和醪糟的香气真让人陶醉，吃起来蛋花柔软，滑嫩爽口，香甜略酸，真是色香味俱全，各种干鲜果星星点点，色彩斑斓，能窥其一斑之人，便觉口水四溢，实乃罕世佳肴。相比热水冲调的蛋花米酒，确实别有一番风韵啊。</p>\r\n<p>\r\n	　　鸡蛋牛奶醪糟　　</p>\r\n<center>\r\n	<img alt="" height="403" src="http://www.364000.com/uploads/allimg/140818/7-140QQG450.jpg" width="480" /></center>\r\n<p>\r\n	　　甜食还有冻梨、八宝饭等等，不一而足。</p>\r\n<p>\r\n	　　冻梨　　</p>\r\n<center>\r\n	<img alt="" height="280" src="http://www.364000.com/uploads/allimg/140818/7-140QQG450-50.jpg" width="480" /></center>\r\n<p>\r\n	　　7.通渭路的洋芋片和豆皮绝对是兰州小小吃的翘楚。这种即做即吃的美食竟然开起了某宝店支持快运，真是让我大跌眼镜，也说明人们对他的喜爱。简简单单的麻辣洋芋片和麻辣豆皮，竟然能做出如此让人欲罢不能的味道，真不理解他们是怎么做到的。这里还卖炸年糕，还有残留着小时候记忆的胡萝卜素。大家 yy 下吧.</p>\r\n<p>\r\n	　　通渭路洋芋片和胡萝卜素　　</p>\r\n<center>\r\n	<img alt="" height="266" src="http://mmbiz.qpic.cn/mmbiz/Ag6LhpVmuEPeTWw1eSnHOmH8VS4we2ooPqIAkFT3KNvIjhXWTP7X9nqVyT22QpicYPOXKuV2cnicPW22iatUXsxyA/640" width="480" /></center>\r\n<p>\r\n	　　豆皮　　</p>\r\n<center>\r\n	<img alt="" height="631" src="http://www.364000.com/uploads/allimg/140818/7-140QQG451-50.jpg" width="480" /></center>\r\n<p>\r\n	　　炸年糕　　</p>\r\n<center>\r\n	<img alt="" height="354" src="http://www.364000.com/uploads/allimg/140818/7-140QQG452.jpg" width="480" /></center>\r\n<p>\r\n	　　8.来到兰州的夜市，绝对可以让你扶墙进去，扶墙出来。兰州的羊肉泡馍和西安的大有不同，西安泡沫用死面(没有发酵的面)做的饼，而兰州的用一种发面饼，它采用一种特殊的发酵工艺，保持了面粉的原始香味，任何佐料不放时都很可口，放进姜黄(一种食用色素)和苦豆(一种有淡淡苦味的草类晾干磨碎使用)就更不得了。西安泡馍需自己掰碎然后回锅做好，而兰州泡馍只管做好汤给你放好肉，然后塞给你一个大饼，然后你就自便了，你可以盘起腿，或一股脑儿将饼放进汤里，或一口汤一口饼。</p>\r\n<p>\r\n	　　羊肉泡馍　　</p>\r\n<center>\r\n	<img alt="" height="356" src="http://www.364000.com/uploads/allimg/140818/7-140QQG452-50.jpg" width="480" /><br />\r\n	#p#副标题#e#</center>\r\n<p>\r\n	　　9.夜市美食第二弹&mdash;&mdash;羊杂碎。正宗的全羊杂碎之主料是羊的心、肝、肺，下锅的时候切成碎丁或薄片。副料是肠(生油的)、肚(生味的)、头蹄肉(架碗充数的)，下锅时要切成细丝和长条。然后放在一起炒熟，杂碎酥烂绵软，醇美味存于汤.</p>\r\n<p>\r\n	　　羊杂碎　　</p>\r\n<center>\r\n	<img alt="" height="604" src="http://www.364000.com/uploads/allimg/140818/7-140QQG453.jpg" width="480" /></center>\r\n<p>\r\n	　　10.西北烤肉大家都听说过，不过如果只是烤肉，也就不值得我单提出来说了。兰州夜市的烤肉都是挂一只羊腿，现场片肉片穿起来烤的，保证货真价实。另外，酸辣羊肚、烤羊排、牛板筋、鸡胗、蹄筋、羊尾骨、羊头、还有整个的大腰子，也许别处是吃不到的。</p>\r\n<p>\r\n	　　还没开始烤的烤肉　　</p>\r\n<center>\r\n	<img alt="" height="360" src="http://www.364000.com/uploads/allimg/140818/7-140QQG453-50.jpg" width="480" /></center>\r\n<p>\r\n	　　11.浆水面我还真没在南方见到过。估计大部分南方的同学也不知道浆水是什么。浆水就是用包菜、芹菜、苦渠、萝卜、土豆、黄豆芽等为原料(还有其他原料，依口味习惯选择一样，也可以是几样搭配而作)，在沸水里烫过后，加酵母发酵而成。夏天炎热的时候，人们食欲不振，一碗冰凉消暑的浆水面绝对可以让你大快朵颐，酸爽消暑，远胜于凉面。浆水面 + 猪蹄子 + 排骨。哎。坐等假期回家了。</p>\r\n<p>\r\n	　　浆水面　　</p>\r\n<center>\r\n	<img alt="" height="359" src="http://www.364000.com/uploads/allimg/140818/7-140QQG455.jpg" width="480" /></center>\r\n<p>\r\n	　　12.油炒粉。油炒粉是用卤猪油炒制的凉粉，先在平底铁锅内烧开卤猪油，再把凉粉切成麻将牌大的方块粉分次放入锅内，加糖、葱花、蒜苗、精盐、酱油，不断翻炒而成。</p>\r\n<p>\r\n	　　油炒粉　</p>\r\n<center>\r\n	<img alt="" height="351" src="http://www.364000.com/uploads/allimg/140818/7-140QQG456.jpg" width="480" /></center>\r\n<p>\r\n	　　金城酿皮、擀面皮和牛筋面皮、马爷炒面、手抓羊肉、黄焖羊肉、浆水漏鱼、杏皮水&hellip;&hellip;。哎，好吃的太多，实在写不动了。</p>\r\n<p>\r\n	　　酿皮　　</p>\r\n<center>\r\n	<img alt="" height="309" src="http://www.364000.com/uploads/allimg/140818/7-140QQG456-50.jpg" width="480" /></center>\r\n<p>\r\n	　　马爷炒面片　　</p>\r\n<center>\r\n	<img alt="" height="363" src="http://www.364000.com/uploads/allimg/140818/7-140QQG457.jpg" width="480" /></center>\r\n<p>\r\n	　　手抓羊肉　　</p>\r\n<center>\r\n	<img alt="" height="380" src="http://www.364000.com/uploads/allimg/140818/7-140QQG457-50.jpg" width="480" /></center>\r\n<p>\r\n	　　黄焖羊肉　　</p>\r\n<center>\r\n	<img alt="" height="352" src="http://www.364000.com/uploads/allimg/140818/7-140QQG458.jpg" width="480" /></center>\r\n<p>\r\n	　　浆水漏鱼　　</p>\r\n<center>\r\n	<img alt="" height="338" src="http://www.364000.com/uploads/allimg/140818/7-140QQG458-50.jpg" width="480" /></center>\r\n<p>\r\n	　　当然，还有驰名中外的牛肉面啦。朋友的同学来兰州玩，走的时候说了这么一句话：在兰州，最大的感受就是，站在这家牛肉面馆，一定能看见下一家牛肉面馆。</p>\r\n<p>\r\n	　　兰州牛肉面　　</p>\r\n<center>\r\n	<img alt="" height="358" src="http://www.364000.com/uploads/allimg/140818/7-140QQG458-51.jpg" width="480" /></center>\r\n', '先说说不加辣子和醋的甜食。 不仅南方有甜食，西北的甜食也别具一格。灰豆子是主料是豌豆，配以红枣白糖等，煮成粥状物，味道甜美，又有豆子煮绵的口感，既可冬天趁热喝，又可夏天冰镇。爱甜食的 MM 和小盆友绝', 419, '/uploads/allimg/140818/7_0QQH3193J8.jpg', 1408353269, 'c,p', '', 0, 1, 0),
(29, '9大旅游城市不得不吃的美食！', 5, 0, '9大旅游城市不得不吃的美食！,旅游城市,美食', '9大旅游城市不得不吃的美食！,旅游城市,美食', '无花果', '未知', '<p>\r\n	　　西安、丽江、北京、成都、杭州、三亚、重庆、武汉、长沙，旅途中有了美食的相伴才算得上完美，各位客官，走起了你嘞&hellip;&hellip;　　</p>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00611.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00611-50.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00611-51.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00612.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00612-50.jpg" width="440" /><br />\r\n	#p#副标题#e#<br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00612-51.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00613.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00613-50.jpg" width="440" /><br />\r\n	　　</center>\r\n<center>\r\n	<img alt="" height="440" src="http://www.364000.com/uploads/allimg/140828/7-140RQ00614.jpg" width="440" /></center>\r\n', '西安、丽江、北京、成都、杭州、三亚、重庆、武汉、长沙，旅途中有了美食的相伴才算得上完美，各位客官，走起了你嘞 副标题', 191, '/uploads/allimg/140828/7_0RQ009363116.jpg', 1409191104, 'p', '', 0, 1, 0);

